import React, { useState } from 'react';
import apiClient from './apiClient';

const AddWorkout = () => {
  const [workoutName, setWorkoutName] = useState('');
  const [workout, setWorkout] = useState(null);
  const [exerciseName, setExerciseName] = useState('');

  const handleAddWorkout = async () => {
    try {
      const response = await apiClient.post('/workouts', { name: workoutName });
      setWorkout(response.data);
      setWorkoutName('');
    } catch (error) {
      console.error('Error adding workout:', error);
    }
  };

  const handleAddExercise = async () => {
    if (!workout) {
      alert('Create a workout first.');
      return;
    }

    try {
      const response = await apiClient.post(`/workouts/${workout._id}/exercises`, { name: exerciseName });
      setWorkout(response.data);
      setExerciseName('');
    } catch (error) {
      console.error('Error adding exercise:', error);
    }
  };

  return (
    <div>
      <h2>Add Workout</h2>
      <input
        type="text"
        placeholder="Workout name"
        value={workoutName}
        onChange={(e) => setWorkoutName(e.target.value)}
      />
      <button onClick={handleAddWorkout}>Add Workout</button>

      {workout && (
        <>
          <h3>Add Exercises to {workout.name}</h3>
          <input
            type="text"
            placeholder="Exercise name"
            value={exerciseName}
            onChange={(e) => setExerciseName(e.target.value)}
          />
          <button onClick={handleAddExercise}>Add Exercise</button>

          <h4>Exercises</h4>
          <ul>
            {workout.exercises.map((exercise, index) => (
              <li key={index}>{exercise.name}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default AddWorkout;
