import React, { useEffect, useState } from 'react';
import apiClient from './apiClient';
import { Container, Row, Col, ListGroup, ListGroupItem } from 'react-bootstrap';
import { useAuth } from './AuthContext';
import Accordion from 'react-bootstrap/Accordion';
import { Link } from 'react-router-dom';

function UserWorkouts() {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const auth = useAuth();
  const currentUser = auth.currentUser; // Replace with the actual current user

  useEffect(() => {
    async function fetchWorkouts(){
      try {
        const response = await apiClient.get(`/users/${currentUser}`);
        // let userWorkouts = response.data.workouts;
        // console.log(userWorkouts);

        setWorkouts(response.data.workouts);
      } catch (error) {
        console.error('Error fetching workouts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [currentUser]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <Container>
      <Row className="justify-content-md-center">
        {/* <Col md="auto"> */}
          <h2>{currentUser}'s Workouts</h2>
          <Accordion defaultActiveKey={0}>

            {workouts.map((workout) => (
              <Accordion.Item eventKey="0" key={workout._id}>
                <Accordion.Header>{workout.name}</Accordion.Header>
                <Accordion.Body>
                {workout.exercises.map((exercise) => (
                  <div key={exercise._id}>
                    <p>{exercise.name}</p>
                    <ui>Sets: {exercise.sets} | Reps: {exercise.reps} | Weight: {exercise.weight} lbs</ui>
                  </div>
                ))}
                </Accordion.Body>
              </Accordion.Item>
            ))}
          </Accordion>
        {/* </Col> */}
      </Row>
    </Container>
  );
};

export default UserWorkouts;
