import React, { useEffect, useState } from 'react';
import apiClient from './apiClient';
import { Container, Row, Col, Accordion, Card, Button } from 'react-bootstrap';
import { useAuth } from './AuthContext';

const UserWorkouts = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const auth = useAuth();
  const currentUser = auth.currentUser; // Replace with the actual current user

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        const response = await apiClient.get(`/users/${currentUser}`);
        setWorkouts(response.data.workouts);
      } catch (error) {
        console.error('Error fetching workouts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [currentUser]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col md="auto">
          <h2>{currentUser}'s Workouts</h2>
          <Accordion>
            {workouts.map((workout) => (
              <Card key={workout._id}>
                <Card.Header>
                  <Accordion.Toggle as={Button} variant="link" eventKey={workout._id}>
                    {workout.name}
                  </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey={workout._id}>
                  <Card.Body>
                    {workout.exercises.map((exercise) => (
                      <div key={exercise._id}>
                        <h5>{exercise.name}</h5>
                        <p>
                          Sets: {exercise.sets}, Reps: {exercise.reps}, Weight: {exercise.weight}
                        </p>
                      </div>
                    ))}
                  </Card.Body>
                </Accordion.Collapse>
              </Card>
            ))}
          </Accordion>
        </Col>
      </Row>
    </Container>
  );
};

export default UserWorkouts;
