import React, { useState } from 'react';
import apiClient from './apiClient';
import { useAuth } from './AuthContext';



const RemoveWorkout = () => {

  const [workoutName, setWorkoutName] = useState('');

  const auth = useAuth();
  const currentUser = auth.currentUser;

  const handleAddExercise = () => {
    const newExercise = {
      name: exerciseName,
      sets: parseInt(exerciseSets),
      reps: parseInt(exerciseReps),
      weight: parseFloat(exerciseWeight),
    };
    setExercises([...exercises, newExercise]);
    setExerciseName('');
    setExerciseSets('');
    setExerciseReps('');
    setExerciseWeight('');
  };

  const undoExercise = () => {
    if(exercises.length > 0){
        setExercises(exercises.slice(0,exercises.length-1))
    }

  }

  const handleSubmitWorkout = async () => {
    const workout = {
      name: workoutName,
      exercises: exercises,
    };

    try {
      await apiClient.post(`/users/${currentUser}/workouts`, workout);
      setWorkoutName('');
      setExercises([]);
    } catch (error) {
      console.error('Error submitting workout:', error);
    }
  };

  return (
    <div>
      <h2>Workout Name</h2>
      <input
        type="text"
        placeholder="Workout name"
        value={workoutName}
        onChange={(e) => setWorkoutName(e.target.value)}
      />
      <button onClick={handleSubmitWorkout}>Remove Workout</button>
    </div>
  );
};

export default RemoveWorkout;
