import React, { useEffect, useState } from 'react';
import apiClient from './apiClient';
import { Container, Row, Col, ListGroup, ListGroupItem } from 'react-bootstrap';
import { useAuth } from './AuthContext';

const UserWorkouts = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const auth = useAuth();
  const currentUser = auth.currentUser; // Replace with the actual current user

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        const response = await apiClient.get(`/users/${currentUser}/workouts`);
        setWorkouts(response.data);
      } catch (error) {
        console.error('Error fetching workouts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [currentUser]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col md="auto">
          <h2>{currentUser}'s Workouts</h2>
          <ListGroup>
            {workouts.map((workout) => (
              <ListGroupItem key={workout._id}>{workout.name}</ListGroupItem>
            ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
};

export default UserWorkouts;
