import React, { useState } from 'react';
import apiClient from './apiClient';
import { useAuth } from './AuthContext';
import { Container, Row, Col, Form, Button, ListGroup } from 'react-bootstrap';

const AddWorkout = () => {
  // ... (existing state and functions)

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col md="auto">
          <h2>Add Workout</h2>
          <Form>
            <Form.Group controlId="workoutName">
              <Form.Label>Workout name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Workout name"
                value={workoutName}
                onChange={(e) => setWorkoutName(e.target.value)}
              />
            </Form.Group>

            <h3>Add Exercises</h3>
            <Form.Group controlId="exerciseName">
              <Form.Label>Exercise name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Exercise name"
                value={exerciseName}
                onChange={(e) => setExerciseName(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="exerciseSets">
              <Form.Label>Sets</Form.Label>
              <Form.Control
                type="number"
                placeholder="Sets"
                value={exerciseSets}
                onChange={(e) => setExerciseSets(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="exerciseReps">
              <Form.Label>Reps</Form.Label>
              <Form.Control
                type="number"
                placeholder="Reps"
                value={exerciseReps}
                onChange={(e) => setExerciseReps(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="exerciseWeight">
              <Form.Label>Weight</Form.Label>
              <Form.Control
                type="number"
                placeholder="Weight"
                value={exerciseWeight}
                onChange={(e) => setExerciseWeight(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" onClick={handleAddExercise}>
              Add Exercise
            </Button>
          </Form>

          <h4>Exercises</h4>
          <ListGroup>
            {exercises.map((exercise, index) => (
              <ListGroup.Item key={index}>
                {exercise.name}, {exercise.sets} sets, {exercise.reps} reps, {exercise.weight} lbs
              </ListGroup.Item>
            ))}
          </ListGroup>

          <Button variant="success" onClick={handleSubmitWorkout} className="mt-3">
            Submit Workout
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default AddWorkout;
